public static void unserialize(String base) throws IOException, ClassNotFoundException {
    byte[] result = Base64.getDecoder().decode(base);
    ByteArrayInputStream byteArrayInputStream = new ByteArrayInputStream(result);
    ObjectInputStream objectInputStream = new ObjectInputStream(byteArrayInputStream);
    objectInputStream.readObject();
}

public static String serialize(Object object) throws IOException {
    ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
    ObjectOutputStream objectOutputStream = new ObjectOutputStream(byteArrayOutputStream);
    objectOutputStream.writeObject(object);
    return Base64.getEncoder().encodeToString(byteArrayOutputStream.toByteArray());
}

public static Field getField(Class<?> clazz, String fieldName) {
    Field field = null;
    try {
        field = clazz.getDeclaredField(fieldName);
        field.setAccessible(true);
    } catch (NoSuchFieldException e) {
        if (clazz.getSuperclass() != null) {
            field = getField(clazz.getSuperclass(), fieldName);
        }
    }
    return field;
}

public static Object getFieldValue(Object obj, String fieldName) throws Exception {
    Field field = obj.getClass().getDeclaredField(fieldName);
    field.setAccessible(true);
    return field.get(obj);
}

public static Object newInstance(String className, Object... args) throws Exception {
        Constructor<?> ctor = Class.forName(className).getDeclaredConstructors()[0];
        ctor.setAccessible(true);
        return ctor.newInstance(args);
    }

public static void setFieldValue(Object object, String field, Object arg) throws NoSuchFieldException, IllegalAccessException {
    Field f = object.getClass().getDeclaredField(field);
    f.setAccessible(true);
    f.set(object, arg);
}

public static Constructor<?> getFirstCtor(String name) throws Exception {
    Constructor<?> ctor = Class.forName(name).getDeclaredConstructors()[0];
    ctor.setAccessible(true);
    return ctor;
}

